import React, { Component } from "react";
import "./About.css";
import fanvision from "../../img/fanvision_screen.png";
import adamovies from "../../img/adamovies_screen.png";
import { navigate } from '@reach/router';

class Courses extends Component {
    constructor(props) {
      super(props);
      this.state = {
        courses: 'cs',
      };
    }

    changeCourses = () => {
        if (this.state.courses === 'cs'){
            this.setState({courses: 'all'});
        }else{
            this.setState({courses: 'cs'});
        }
    }

    render() {
      return (
        <div className='Courses-container u-bordering'>
            <div className='courses-subcontainer u-bordering'>
                <h1 className='u-textCenter'>Courses</h1>
                <h3 onClick={this.changeCourses} className='show-button u-textCenter'>{this.state.courses === 'cs'? 'Show All Courses' : 'Show Relevant Courses'}</h3>
                <div className='single-class'>
                    <h3>Spring 2021</h3>
                    <div>6.031 - Elements of Software Construction</div>
                    <div>6.036 - Intro to Machine Learning</div>
                    <div>6.08 - Intro to EECS via Interconnected Embedded Systems</div>
                    {this.state.courses === 'all' && <div>3.091 - Intro to Solid State Chemistry</div>}
                </div>
                <div className='single-class'>
                    <h3>Fall 2020</h3>
                    <div>6.006 - Intro to Algorithms</div>
                    <div>6.004 - Computation Structures</div>
                    <div>18.06 - Linear Algebra</div>
                    <div>18.600 - Probability and Random Variables</div>
                    {this.state.courses === 'all' && <div>21W.747 - Rhetoric</div>}
                </div>
                <div className='single-class'>
                    <h3>Spring 2020</h3>
                    <div>6.009 - Fundamentals of Programming</div>
                    <div>6.042 - Math for Computer Science</div>
                    {this.state.courses === 'all' && <div>8.02 - Physics II</div>}
                    {this.state.courses === 'all' && <div>21M.051 - Fundamentals of Music</div>}
                </div>
                {this.state.courses === 'all' && <div className='single-class'>
                    <h3>Fall 2019</h3>
                    <div>6.0001/2 - Intro to Computer Science</div>
                    <div>18.02 - Multivariable Calculus</div>
                    <div>8.01 - Physics I</div>
                    <div>21W.022 -  Reading and Writing Autobiography</div>
                </div>}
            </div>
            <div className='projects-container u-bordering'>
                <h1 className='u-textCenter'>Projects</h1>
                <div className='site-card' >
                <div className='name-div'><a className='u-linked title-text'>Adamovies</a></div>
                    <img src={adamovies} className='site-img'/>
                    <div>Adamovies is a ...</div>
                </div>
                <div className='site-card' >
                    <div className='name-div'><a className='u-linked title-text'>Fanvision</a></div>
                    <img src={fanvision} className='site-img'/>
                    <div>Fanvision is a ...</div>
                </div>
            </div>
        </div>
      );
    }
  }

  export default Courses;
