import React, { Component } from "react";
import "./About.css";

class Courses extends Component {
    constructor(props) {
      super(props);
      this.state = {
        courses: 'cs',
      };
    }

    changeCourses = () => {
        if (this.state.courses === 'cs'){
            this.setState({courses: 'all'});
        }else{
            this.setState({courses: 'cs'});
        }
    }

    render() {
      return (
        <div className='Courses-container u-bordering'>
            <h1 className='u-textCenter'>Courses</h1>
            <h3 className='show-button u-textCenter'>{this.state.courses === 'cs'? 'Show All Courses' : 'Show Relevant Courses'}</h3>
        </div>
      );
    }
  }

  export default Courses;
